namespace Leben;public partial class MainPage : ContentPage{	public MainPage()	{		InitializeComponent();		string directórioRaíz = "D:\\EXT\\Desenvolvimento\\Leben\\Dado";		List<string> directórios = Directory.EnumerateDirectories(directórioRaíz).ToList();        int médiaDaGrade = (int)Math.Ceiling((decimal)directórios.Count / 2);        int linhas = médiaDaGrade;        int columnas = médiaDaGrade;                Grid QuadroTemporal = new Grid();        QuadroTemporal.BackgroundColor = Colors.White;        for (int i = 0; i < linhas; i++)        {            RowDefinition linha = new RowDefinition();            linha.Height = GridLength.Star;            QuadroTemporal.AddRowDefinition(linha);        }        for (int i = 0; i < columnas; i++)         {            ColumnDefinition columna = new ColumnDefinition();            columna.Width = GridLength.Star;            QuadroTemporal.AddColumnDefinition(columna);        }        foreach (var directório in directórios)        {            Frame período = new Frame();            período.Background = new LinearGradientBrush([                new GradientStop(Color.FromRgb(200, 0, 0), 0),                new GradientStop(Color.FromRgb(0, 0, 200), 1)            ]);            período.CornerRadius = 0;            período.Padding = 0;            período.Margin = 0;            QuadroTemporal.Add(período, 0, 0);        }        Contentor.Content = QuadroTemporal;        Contentor.Padding = 0;    }    private void ClickGestureRecognizer_Clicked(object sender, TappedEventArgs e)    {		Frame quadro = (Frame)sender;		quadro.Animate(            name: "MudaFundo",            animation: new Animation(v => ((LinearGradientBrush)quadro.Background).GradientStops[0].Offset = (float)v, 0, 1, Easing.Default),            rate: 8,             length: 4000,            easing: Easing.Linear,            finished: null,            repeat: () => false			);    }}